import "./index.css";
import { Footer } from "./footer";
import { useState, useEffect } from "react";

const initialPizzas = [
  {
    name: "Focaccia",
    ingredients: "Bread with Italian olive oil and rosemary",
    price: 6,
    photo: "pizzas/focaccia.jpg",
    soldOut: false,
  },
  {
    name: "Margherita",
    ingredients: "Tomato sauce, mozzarella, and basil",
    price: 8,
    photo: "pizzas/margherita.jpg",
    soldOut: false,
  },
  {
    name: "Pepperoni",
    ingredients: "Tomato sauce, mozzarella, and pepperoni",
    price: 9,
    photo: "pizzas/pepperoni.jpg",
    soldOut: false,
  },
  {
    name: "BBQ Chicken",
    ingredients: "BBQ sauce, chicken, red onions, and cilantro",
    price: 10,
    photo: "",
    soldOut: false,
  },
  {
    name: "Hawaiian",
    ingredients: "Tomato sauce, mozzarella, ham, and pineapple",
    price: 9,
    photo: "",
    soldOut: true,
  },
  {
    name: "Veggie",
    ingredients: "Tomato sauce, mozzarella, bell peppers, olives, and onions",
    price: 8,
    photo:
      "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSTV3cHGbfm27YM9fmyhy9e9smzjXld34oamw&s",
    soldOut: false,
  },
  {
    name: "Buffalo",
    ingredients: "Buffalo sauce, chicken, mozzarella, and blue cheese",
    price: 11,
    photo: "pizzas/buffalo.jpg",
    soldOut: false,
  },
  {
    name: "Four Cheese",
    ingredients: "Tomato sauce, mozzarella, parmesan, gouda, and gorgonzola",
    price: 12,
    photo: "pizzas/four-cheese.jpg",
    soldOut: false,
  },
  {
    name: "Meat Lovers",
    ingredients: "Tomato sauce, mozzarella, pepperoni, sausage, and bacon",
    price: 13,
    photo: "pizzas/meat-lovers.jpg",
    soldOut: true,
  },
  {
    name: "Spinach & Feta",
    ingredients: "Olive oil, spinach, feta, and garlic",
    price: 9,
    photo: "pizzas/spinach-feta.jpg",
    soldOut: false,
  },
];

// Sort pizzas by name
const sortedPizzas = initialPizzas.sort((a, b) => a.name.localeCompare(b.name));

export default function App() {
  const [pizzas, setPizzas] = useState(sortedPizzas);
  const [isOpen, setIsOpen] = useState(true);
  const [isToggled, setIsToggled] = useState(false);

  const openHours = 9;
  const hour = new Date().getHours();
  const shopOpen = hour >= openHours;

  useEffect(() => {
    if (isToggled) {
      document.body.classList.add("new-style");
    } else {
      document.body.classList.remove("new-style");
    }
  }, [isToggled]);

  // Function to delete a pizza
  const deletePizza = (pizzaName) => {
    setPizzas(pizzas.filter((pizza) => pizza.name !== pizzaName));
  };

  return (
    <>
      <div className="buttons">
        <button className="close" onClick={() => setIsOpen(!isOpen)}>
          &times;
        </button>
        <button
          className="toggle-button"
          onClick={() => setIsToggled(!isToggled)}
        >
          &clubs;
        </button>
      </div>

      {true ? (
        <div>
          {isOpen && (
            <ul className="pizza-container">
              {pizzas.map((pizza) => (
                <PizzaItem
                  key={pizza.name}
                  pObj={pizza}
                  isToggled={isToggled}
                  onDelete={deletePizza}
                />
              ))}
            </ul>
          )}
        </div>
      ) : (
        <span
          style={{
            alignContent: "center",
            justifyContent: "center",
            display: "flex",
            margin: "10%",
          }}
        >
          😍Built with Love from Burgeon
        </span>
      )}

      <Footer />
    </>
  );
}

function PizzaItem({ pObj, isToggled, onDelete }) {
  return (
    <li className={`pizza-div ${isToggled ? "toggled-style" : ""}`}>
      <div className="pizza-photo">
        <img src={pObj.photo} alt={pObj.name} />
      </div>
      <div className="pizza-info">
        <h3>{pObj.name}</h3>
        <p>{pObj.ingredients}</p>
        <p className="price">${pObj.price}</p>
      </div>
      <button className="delete-button" onClick={() => onDelete(pObj.name)}>
        Delete
      </button>
    </li>
  );
}
