import React from "react";
import ReactDOM from "react-dom";
import "./index.css";

//data array objects
const pizzaData = [
  {
    name: "Focaccia",
    ingredients: "Bread with italian olive oil and rosemary",
    price: 6,
    photoName: "pizzas/focaccia.jpg",
    soldOut: false,
  },
  {
    name: "Pizza Margherita",
    ingredients: "Tomato and mozarella",
    price: 10,
    photoName: "pizzas/margherita.jpg",
    soldOut: false,
  },
  {
    name: "Pizza Spinaci",
    ingredients: "Tomato, mozarella, spinach, and ricotta cheese",
    price: 12,
    photoName: "pizzas/spinaci.jpg",
    soldOut: false,
  },
  {
    name: "Pizza Funghi",
    ingredients: "Tomato, mozarella, mushrooms, and onion",
    price: 12,
    photoName: "pizzas/funghi.jpg",
    soldOut: false,
  },
  {
    name: "Pizza Salamino",
    ingredients: "Tomato, mozarella, and pepperoni",
    price: 15,
    photoName: "pizzas/salamino.jpg",
    soldOut: true,
  },
  {
    name: "Pizza Prosciutto",
    ingredients: "Tomato, mozarella, ham, aragula, and burrata cheese",
    price: 18,
    photoName: "pizzas/prosciutto.jpg",
    soldOut: false,
  },
];

function App() {
  return (
    <div className="container">
      <Menu />
    </div>
  );
}

const Menu = () => {
  return (
    <div>
      {pizzaData.map((pizza) => (
        <PizzaList pizzaObj={pizza} key={pizza.name} />
      ))}
    </div>
  );
};

const PizzaList = (props) => {
  return (
    <div className="pizza-container">
      <img className="pizza-img" src="pizzas/focaccia.jpg" alt="pizza-image" />

      <div className="info">
        <h2 className="pizza-title">{props.pizzaObj.name}</h2>
        <p className="pizza-description">{props.pizzaObj.name}</p>
        <p className="pizza-price">{props.pizzaObj.name}</p>
        <div>Status</div>
        <div className="button-container">
          <button className="btn">Add</button>
          <button className="delete-btn">Delete</button>
        </div>
      </div>
    </div>
  );
};

//react version 18
const root = ReactDOM.createRoot(document.getElementById("root"));
root.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>
);

//react before 18
