export function Footer() {
  const date = new Date().toDateString();
  const hour = new Date().getHours();
  const openHours = 9;
  const closeHours = 12;
  const isOpen = hour >= openHours;

  return (
    <>
      <div
        style={{
          backgroundColor: "darkred",
          color: "white",
          width: "80%",
          margin: "0 auto",
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          gap: "50%",
        }}
      >
        {isOpen ? <h3>We are currently Open Now</h3> : ""}
        <p
          style={{
            fontWeight: "bold",
          }}
        >
          {date}
        </p>
      </div>
    </>
  );
}
