export function Footer() {
  const hour = new Date().toDateString();
  return (
    <>
      <div
        style={{
          backgroundColor: "darkred",
          color: "white",
          width: "80%",
          margin: "0 auto",
          display: "flex",
          justifyContent: "center",
          flexDirection: "row",
        }}
      >
        <h2>We are currently Open Now</h2>
        <p>{hour}</p>
      </div>
    </>
  );
}
