import "./index.css";
import { Footer } from "./footer";
import { useState, useEffect } from "react";

const pizzas = [
  {
    name: "Focaccia",
    ingredients: "Bread with Italian olive oil and rosemary",
    price: 6,
    photo: "pizzas/focaccia.jpg",
    soldOut: false,
  },
  {
    name: "Margherita",
    ingredients: "Tomato sauce, mozzarella, and basil",
    price: 8,
    photo: "pizzas/margherita.jpg",
    soldOut: false,
  },
  {
    name: "Pepperoni",
    ingredients: "Tomato sauce, mozzarella, and pepperoni",
    price: 9,
    photo: "pizzas/pepperoni.jpg",
    soldOut: false,
  },
  {
    name: "BBQ Chicken",
    ingredients: "BBQ sauce, chicken, red onions, and cilantro",
    price: 10,
    photo: "",
    soldOut: false,
  },
  {
    name: "Hawaiian",
    ingredients: "Tomato sauce, mozzarella, ham, and pineapple",
    price: 9,
    photo: "",
    soldOut: true,
  },
  {
    name: "Veggie",
    ingredients: "Tomato sauce, mozzarella, bell peppers, olives, and onions",
    price: 8,
    photo:
      "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSTV3cHGbfm27YM9fmyhy9e9smzjXld34oamw&s",
    soldOut: false,
  },
  {
    name: "Buffalo",
    ingredients: "Buffalo sauce, chicken, mozzarella, and blue cheese",
    price: 11,
    photo: "pizzas/buffalo.jpg",
    soldOut: false,
  },
  {
    name: "Four Cheese",
    ingredients: "Tomato sauce, mozzarella, parmesan, gouda, and gorgonzola",
    price: 12,
    photo: "pizzas/four-cheese.jpg",
    soldOut: false,
  },
  {
    name: "Meat Lovers",
    ingredients: "Tomato sauce, mozzarella, pepperoni, sausage, and bacon",
    price: 13,
    photo: "pizzas/meat-lovers.jpg",
    soldOut: true,
  },
  {
    name: "Spinach & Feta",
    ingredients: "Olive oil, spinach, feta, and garlic",
    price: 9,
    photo: "pizzas/spinach-feta.jpg",
    soldOut: false,
  },
];

const pizzadata = pizzas;

export default function App() {
  const [isToggled, setIsToggled] = useState(false); // State to toggle body style

  // Effect to update the body class based on the state
  useEffect(() => {
    if (isToggled) {
      document.body.classList.add("new-style"); // Add class when toggled
    } else {
      document.body.classList.remove("new-style"); // Remove class when not toggled
    }
  }, [isToggled]); // Depend on the state so it updates when toggled

  return (
    <>
      <button
        className="toggle-button"
        onClick={() => setIsToggled(!isToggled)}
      >
        Toggle Body Style
      </button>

      <ul className="pizza-container">
        {pizzadata.map((pizza) => (
          <PizzaItem pObj={pizza} key={pizza.name} />
        ))}
      </ul>

      <Footer />
    </>
  );
}

function PizzaItem(p) {
  return (
    <li className="pizza-div">
      <div className="pizza-photo">
        <img src={p.pObj.photo} alt={p.pObj.name} />
      </div>
      <div className="pizza-info">
        <h3>{p.pObj.name}</h3>
        <p>{p.pObj.ingredients}</p>
        <p className="price">${p.pObj.price}</p>
      </div>
      <button className="delete-button">Delete</button>
    </li>
  );
}
